<?phpclass BackupUtil { protected $core = null; function __construct($core) { $this->core = $core; } function checkAuth() { $ans = array('result' => '', 'status' => false, 'message' => '', 'error' => false); try { $us = new UserService(); $result = $us->isAuthenticated(); if (!is_null($result->result) && $result->status) { $ans['status'] = true; } } catch (Exception $e) { $ans['message'] = $e->getMessage(); } return $ans; } function scanBackups() { } }