<?php
class Moto_Html_Abstract { protected $_options = array(); protected $_defaultOptions = array(); function __construct($options = null) { $this->setOptions($this->_defaultOptions); if (is_array($options)) $this->setOptions($options); } function getOption($name, $defalut = null) { return ( isset($this->_options[$name]) ? $this->_options[$name] : $defalut ); } function setOption($name, $value) { $this->_options[$name] = $value; } function setOptions($options) { foreach($options as $name => $value) $this->setOption ($name, $value); } function getOptions() { return $this->_options; } }