<?php
class Moto_Html_Render_Widget_Abstract extends Moto_Html_Render_Abstract { protected $_module = null; protected $_service = null; function proccess($obj, $parent = null) { $this->_module = $obj; $this->_parent = $parent; $html = $this->_proccess($obj); return $html; } protected function _proccess($obj) { } function initModuleData($module) { return $module; } function getProperty($name, $default = array('value' => '')) { return ( $this->_module != null && isset($this->_module->properties) && isset($this->_module->properties[$name]) ? $this->_module->properties[$name] : $default); } function getPropertyValue($name, $default = '') { $property = $this->getProperty($name, array('value' => $default)); return $property['value']; } } 