<?php
 class LayoutTypeVO extends HoldersStackVO { public $id; public $name; public $backgroundImage; public $preview; public function __construct(DOMNode $parent = null) { parent::__construct($parent); if (!empty($parent)) { $this->loadDomElement($parent); } } public function loadDomElement(DOMNode $parent) { parent::loadDomElement($parent); $this->id = (integer) $parent->getAttribute('id'); $this->backgroundImage = (string) $parent->getAttribute('backgroundImage'); $this->preview = (string) $parent->getAttribute('preview'); $node = MotoXML::findOneByXPath("./name", $parent); if (!is_null($node)) $this->name = (string) $node->nodeValue; return $this; } public function saveDomElement(DOMNode $parent) { parent::saveDomElement($parent); return $parent; } public static function findById($id, DOMNode $context) { return MotoXML::findOneByXPath(".//layout[@id='{$id}']", $context, get_class()); } public static function findAll(DOMNode $context) { return MotoXML::findByXPath(".//layout", $context, get_class()); } }