<?php
 class StructureVO { public $websiteStructure; public $layoutsStructure; public $pagesStructure; public $slotsStructure; public $modulesStructure; public $popupsStructure; public $holdersStructure; public function __construct(DOMNode $parent = null) { if (!empty($parent)) { $this->loadDomElement($parent); } } public function loadDomElement(DOMNode $parent) { if (!is_null($node = MotoXML::findOneByXPath('.//website', $parent))) $this->websiteStructure = new WebsiteHoldersStackVO($node); if (!is_null($node = MotoXML::findOneByXPath('.//layouts', $parent))) $this->layoutsStructure = LayoutTypeVO::findAll($node); if (!is_null($node = MotoXML::findOneByXPath('.//pages', $parent))) $this->pagesStructure = PageTypeVO::findAll($node); if (!is_null($node = MotoXML::findOneByXPath('./slots', $parent))) $this->slotsStructure = SlotTypeVO::findAll($node); if (!is_null($node = MotoXML::findOneByXPath('./modules', $parent))) $this->modulesStructure = ModuleTypeVO::findAll($node); if (!is_null($node = MotoXML::findOneByXPath('.//popups', $parent))) $this->popupsStructure = PopupTypeVO::findAll($node); if (!is_null($node = MotoXML::findOneByXPath('./contentHolders', $parent))) $this->holdersStructure = ContentHolderTypeVO::findAll($node); return $this; } public function saveDomElement(DOMNode $parent) { return $parent; } }