<?php
 class VersionVO { public $currentVersion; public $newVersion; public $url; public $description; public function __construct($parent = null) { if (!empty($parent)) { $this->loadDomElement($parent); } $this->currentVersion = MotoVersion::getVersion(); } public function loadDomElement($parent) { if (isset($parent['newVersion'])) $this->newVersion = (string) $parent['newVersion']; if (isset($parent['description'])) $this->description = (string) $parent['description']; if (isset($parent['url'])) $this->url = (string) $parent['url']; else $this->url = (defined('CMS_ACCOUNTS_URI') ? CMS_ACCOUNTS_URI : ''); return $this; } public function saveDomElement(DOMNode $parent) { return $parent; } } ?>