<?php
 class WebsiteHoldersStackVO extends HoldersStackVO { public $defaultPreloaderLinkageName; public $properties; public $loginFormType; public function __construct(DOMNode $parent = null) { parent::__construct($parent); if (!empty($parent)) { $this->loadDomElement($parent); } } public function loadDomElement(DOMNode $parent) { parent::loadDomElement($parent); $this->defaultPreloaderLinkageName = (string) $parent->getAttribute('defaultPreloaderLinkageName'); $this->properties = PropertyTypeVO::findAll($parent); if (!is_null($node = MotoXML::findOneByXPath('./elements/loginForm', $parent))) $this->loginFormType = new LoginFormTypeVO($node); return $this; } public function saveDomElement(DOMNode $parent) { parent::saveDomElement($parent); return $parent; } }