<?php
 class FormAttachItemVO extends FormItemVO { public $allowedExtensions = "jpg,jpeg,png"; public $maximumFileSize = 1000; public function loadDomElement(DOMNode $parent) { parent::loadDomElement($parent); $nodeValue = $parent->getElementsByTagName('allowedExtensions')->item(0); if (!is_null($nodeValue)) $this->allowedExtensions = (string) $nodeValue->nodeValue; $nodeValue = $parent->getElementsByTagName('maximumFileSize')->item(0); if (!is_null($nodeValue)) $this->maximumFileSize = (string) $nodeValue->nodeValue; } public function saveDomElement(DOMNode $parent) { parent::saveDomElement($parent); $parent->appendChild(new DOMElement('allowedExtensions', $this->allowedExtensions)); $parent->appendChild(new DOMElement('maximumFileSize', $this->maximumFileSize)); return $parent; } } ?>