<?php
 class MenuModuleItemTypeVO implements MotoDomObjectInterface { public $properties; public $template; public function __construct(DOMNode $parent = null) { if (!empty($parent)) { $this->loadDomElement($parent); } } public function loadDomElement(DOMNode $parent) { $this->properties = PropertyTypeVO::findAll($parent); $this->template = new MenuModuleItemVO( MotoXML::findOneByXPath("./template", $parent)); return $this; } public function saveDomElement(DOMNode $parent) { } public static function findAll(DOMNode $context) { return MotoXML::findOneByXPath(".//structure", $context, get_class()); } } ?>